/* Placeholder Bootstrap bundle subset (Modal only) */
(function(){
  if(window.bootstrap && window.bootstrap.Modal && window.bootstrap._real) return; // already real
  function Modal(element){
    this._el = element;
  }
  Modal.prototype.show = function(){
    this._el.style.display='block';
    this._el.classList.add('show');
    this._el.removeAttribute('aria-hidden');
  };
  Modal.prototype.hide = function(){
    this._el.classList.remove('show');
    this._el.setAttribute('aria-hidden','true');
    this._el.style.display='none';
  };
  window.bootstrap = window.bootstrap || {};
  window.bootstrap.Modal = window.bootstrap.Modal || Modal;
})();
